svg.script:
var SlideKit = (function() {
    'use strict';

    var currentIndex = 0;

    var module = {};
    module.gotoSlide = function(i) {
        var s = document.querySelector('#slide-' + i);
        if (s) {
            currentIndex = i;
            var svg = document.querySelector('svg');
            svg.setAttribute('viewBox', null);  // reset the user coordinate system
            var bb = s.getBoundingClientRect();
            svg.setAttribute('viewBox', bb.left + ' ' + bb.top + ' ' + bb.width + ' ' + bb.height);
        }
    };
    module.nextSlide = function() {
        module.gotoSlide(currentIndex + 1);
    };
    module.prevSlide = function() {
        module.gotoSlide(currentIndex - 1);
    };

    return module;
})();

// Move to the first slide
SlideKit.gotoSlide(0);

// Key bindings
document.addEventListener('keydown', function(e) {
    if (e.keyCode === 0 || e.keyCode === 39) {
        // next
        SlideKit.nextSlide();
    }
    else if (e.keyCode === 8 || e.keyCode === 37) {
        // previous
        SlideKit.prevSlide();
    }
});
